Scope in Code
public class Name
{
public static void main(String[] args)
{
int x = 0;
//x == 0, y is undefined
if(x == 0)
{
int y = 10;
x = 15;
//x == 15, y == 10
}
//x == 15, y is undefined
}//end method main
}//End class Name
